if(!window.JSON){window.JSON={parse:function(sJSON){return eval("("+sJSON+")")},stringify:function(b){if(b instanceof Object){var d="";if(b.constructor===Array){for(var a=0;a<b.length;a++){d+=this.stringify(b[a])+","}return"["+d.substr(0,d.length-1)+"]"}if(b.toString!==Object.prototype.toString){return'"'+b.toString().replace(/"/g,"\\$&")+'"'}for(var c in b){d+='"'+c.replace(/"/g,"\\$&")+'":'+this.stringify(b[c])+","}return"{"+d.substr(0,d.length-1)+"}"}return typeof b==="string"?'"'+b.replace(/"/g,"\\$&")+'"':String(b)}}};